sys_script_include.script:
var CountPatents = Class.create();
CountPatents.prototype = {
  initialize: function() {
    // Query the "Trucks" table to get the count of each patent
    var truckGr = new GlideRecord('x_1122852_transp_0_trucks'); 
    truckGr.addQuery('patent', 'CONTAINS', 'EVK555'); 
    truckGr.addAggregate('COUNT', 'patent');
    truckGr.groupBy('patent');
    truckGr.query();
    while (truckGr.next()) {
      var count = truckGr.getAggregate('COUNT', 'patent');
      var patent = truckGr.patent.getDisplayValue();
      gs.info('Patent: ' + patent + ', Count: ' + count);
    }
  },
  type: 'CountPatents'
};
